function getNumber(){
    return Math.ceil(Math.random() * 10)
}

class NumPicker extends React.Component{
    render(){
        const num = getNumber();
        let msg;
        if(num == 7){
            msg = 
                <div>
                    <p>ARE YOU HAPPY NOW??!!</p>
                    <img src='data:image/jpeg;base64,'/>
                </div>
        } else{
            msg = 
                <p>BOOHOOOOOOOOOOOOOOOOOO!</p>;
        }
        return(
            <div>
                <h1>Your number is: {num}</h1>

                {/* 1 - simple ternary operator */}
                <p>{num === 7 ? "LUCKY BASTARD!" : "BOOHOO!"}</p>

                {/* 2 - split up ternary operator */}
                {
                    num === 7 
                    ? <img src='data:image/jpeg;base64,'/>
                    : null
                }

                {/* 3 - AND boolean expression */}
                {
                    num === 7 && 
                    <img src='data:image/jpeg;base64,'/>
                }

                {/* 4 - using a variable defined before the return */}
                <p>{msg}</p>
            </div>
        );
    }
}

ReactDOM.render(<NumPicker/>, document.getElementById('root'))